$(window).load(function() {
	if(/Android|webOS|iPhone|iPad|iPod|BlackBerry/i.test(navigator.userAgent)) {
		$('body').addClass('ios');
	}
	else {};
	$('body').removeClass('loaded');
});

function viewport() {
	var e = window,
		a = 'inner';
	if(!('innerWidth' in window)) {
		a = 'client';
		e = document.documentElement || document.body;
	}
	return {
		width: e[a + 'Width'],
		height: e[a + 'Height']
	}
};
$(function() {
	$('input, textarea').each(function() {
		var placeholder = $(this).attr('placeholder');
		$(this).focus(function() {
			$(this).attr('placeholder', '');
		});
		$(this).focusout(function() {
			$(this).attr('placeholder', placeholder);
		});
	});
	$('.button-nav').click(function() {
		$(this).toggleClass('active'), $('.menu').toggleClass('open');
		return false;
	});
	$('.js-link-accordion').click(function() {
		if($(this).hasClass('active')) {
			$('.js-link-accordion').removeClass('active');
			$('.js-accordion').slideUp();
			$(this).removeClass('active'), $(this).parents('.js-parent-accordion').find('.js-accordion').slideUp();
		}
		else {
			$('.js-link-accordion').removeClass('active');
			$('.js-accordion').slideUp();
			$(this).addClass('active'), $(this).parents('.js-parent-accordion').find('.js-accordion').slideDown();
		}
		return false;
	});
	if($('.js-slider-reviews').length) {
		$('.js-slider-reviews').slick({
			dots: true,
			infinite: true,
			speed: 300,
			slidesToShow: 2,
			slidesToScroll: 2,
			adaptiveHeight: true,
			arrows: false,
			responsive: [{
				breakpoint: 991,
				settings: {
					slidesToShow: 1,
					slidesToScroll: 1
				}
			}]
		});
	};
});
var handler = function() {
	var height_footer = $('footer').height();
	var height_header = $('header').height();
	$('.content').css({
		//'padding-top': height_header
	});
	var viewport_wid = viewport().width;
	if(viewport_wid <= 991) {}
	if($('.nav-cont').length) {
		var offset_top = $('.nav-cont').offset().top;
		$(window).scroll(function() {
			if($(window).scrollTop() > offset_top) {
				$('.nav-cont').addClass("fixed");
			}
			else {
				$('.nav-cont').removeClass("fixed")
			}
		});
		$(window).load(function() {
			if($(window).scrollTop() > offset_top) {
				$('.nav-cont').addClass("fixed");
			}
			else {
				$('.nav-cont').removeClass("fixed")
			}
		});
	};
}
$(window).bind('load', handler);
$(window).bind('resize', handler);
$(document).ready(function() {
	Tipped.create('.tooltip-price-left', {
		position: 'leftbottom',
		stem: false,
		offset: {
			x: 10,
			y: -30
		},
		showDelay: 0,
		showOn: 'click, mouseenter'
	});
	Tipped.create('.tooltip-price-right', {
		position: 'rightbottom',
		stem: false,
		offset: {
			x: 10,
			y: -30
		},
		showDelay: 0,
		showOn: 'click, mouseenter'
	});
	$('.fancybox-popup').fancybox({
		type: 'inline',
		helpers: {
			overlay: {
				locked: false
			}
		},
		focus: false
	});

	function activeSection(el) {
		if($(this).scrollTop() >= $(el).offset().top - 60 && $(this).scrollTop() <= $(el).offset().top + $(el).height()) {
			$('.menu__item').removeClass('active');
			$("a[href='" + el + "']").parent('li').addClass('active');
		}
	}
	activeSection('#section-main');
	activeSection('#section-reviews');
	activeSection('#section-license');
	activeSection('#section-compare');
	activeSection('#section-doctors');
	activeSection('#section-faq');
	activeSection('#section-prices');
	$(window).scroll(function(event) {
		activeSection('#section-main');
		activeSection('#section-reviews');
		activeSection('#section-license');
		activeSection('#section-compare');
		activeSection('#section-doctors');
		activeSection('#section-faq');
		activeSection('#section-prices');
	});
	$('.scroll').click(function() {
		var target = $(this.hash);
		if(target.length) {
			var tt = target.offset().top - 60;
			$('html, body').animate({
				scrollTop: tt
			}, 1500);
			if($('.button-nav').hasClass('active')) {
				$('.button-nav').removeClass('active'), $('.menu').removeClass('open');
			}
			return false;
		}
	});
	$(".sform").each(function (index, form) {
	    $(form).find("button").click(function () {
	        var button = $(this);
	        var name = $(form).find("[name='n']");
	        var phone = $(form).find("[name='p']");
	        var content = $(form).find("[name='c']");
	        var bodyPart = $(form).find("[name='b']");
	        var formName = $(form).find("[name='f']");
	        var params = { f: formName.val() };
	        if (name.length > 0) {
	            if (name.val() === null || name.val().trim().length === 0) {
	                window.alert("Введите ваше имя");
	                return;
	            }
	            params["n"] = name.val();
	        }
	        if (phone.length > 0) {
	            if (phone.val() === null || phone.val().trim().length === 0) {
	                window.alert("Введите ваш номер телефона для обратной связи");
	                return;
	            }
	            params["p"] = phone.val();
	        }
	        if (content.length > 0) {
	            if (content.val() === null || content.val().trim().length === 0) {
	                window.alert("Введите ваш вопрос");
	                return;
	            }
	            params["c"] = content.val();
	        }
	        if (bodyPart.length > 0) {
	            if (bodyPart.val() === null || bodyPart.val().trim().length === 0) {
	                window.alert("Введите наименование части тела");
	                return;
	            }
	            params["b"] = bodyPart.val();
	        }
	        button.prop("disabled",true);
	        $.post("/feedback", params, function(data) {
	            button.prop("disabled",false);
                if (data.success === true) {
                    window.alert("Ваша заявка была отправлена. Наш администратор свяжется с вами в ближайшее время");
                    name.val("");
                    phone.val("");
                    content.val("");
                    bodyPart.val("");
                    window.yaCounter42004539.reachGoal($(form).data('goal'));
                } else {
                    window.alert("Не удалось отправить ваше сообщение");
                }
	        }, "json");
	        return false;
	    });
	});
	$(".tooltip-price").click(function () {
	    var classes = $(this).attr("class").split(" ");
	    var containerId = classes[classes.length - 1];
	    var bodyPartName = $("#" + containerId).find(".tooltip-price__title").text();
	    $("[name='b']").val(bodyPartName);
	});
	$("[data-ajax]").each(function (index, item) {
	    $(item).load($(item).data("ajax"));
	});
});